  <script>
    // Atualiza o contador de páginas quando a janela de impressão é aberta
    window.onbeforeprint = function() {
      var pageCount = document.getElementById('page-count');
      pageCount.textContent = '1'; // Define o valor inicial do contador de página como 1
    };

    // Atualiza o contador de páginas quando a página é dividida ao imprimir
    window.onafterprint = function() {
      var pageCount = document.getElementById('page-count');
      pageCount.textContent = ''; // Limpa o valor do contador de página após a impressão
    };
  </script>
